import axios from "axios";

export default {
  // Gets all sessions
  getSessions: function () {
    return axios.get("/api/sessions");
  },
  // Post homework / add comment
  addComment: function (id) {
    return axios.post("/api/sessions/addcomment/" + id);
  }
};
