import React from "react";
import { Link } from "react-router-dom";
import { Button, TextField, Typography, Grid } from "@material-ui/core";

function Login() {
    function handleClick(e) {
        e.preventDefault();
    }
    return (
        <div>
            <h1>If you are an existing client, please log in below.</h1>
            <Grid container alignItems="center" justify="center" alignContent="center" direction="column">

                <TextField name="fullname" display="block" label="Full Name" onChange={this.props.onChange} />
                <TextField name="email" display="block" label="Email Address" onChange={this.props.onChange} />

                <Button component={Link} to="/survey" color="inherit" onClick={this.handleFormSubmit} >Log In</Button>

            </Grid >
        </div>
    );
}

export default Login;
